import React, {useState, useEffect} from 'react';

import logo from './logo.svg';
import './App.css';
import Wallet from './Components/wallet'
import axios from 'axios'



const App = () => {
  const [chain, setChain] = useState()

useEffect(() => {
  axios
    .get('http://localhost:5000/last_block')
    .then(data => {
      setChain(data.last_block)
    })
    .catch(error => {
      console.log("Error: ", error)
    })
}, [chain])

const [changeId, setChangeId] = useState(chain ? chain.transactions.recipient : null)

console.log(changeId)
  return (
    <div className="App">
      <Wallet chain = {setChain} setChangeId = {setChangeId} changeId = {changeId}/>
    </div>
  );
}

export default App;
